function App() {
  const showCart = useSelector(state => state.ui.cartIsVisible);
  const CartItems = useSelector(state=> state.cart.items)
  console.log(CartItems)
  return (
    <Layout>
      {showCart && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
